/* Typedefs */

typedef protocols-type {
type enumeration {
  enum "GRE" {
    value 0;
    description "Transport based on GRE.";
  }
  enum "LDP" {
   value 1;
   description "Transport based on LDP.";
  }
  enum "BGP" {
   value 2;
   description "Transport based on BGP.";
  }
  enum "SR" {
    value 3;
    description "Transport based on Segment Routing";
  }
  enum "SR-TE" {
    value 4;
    description "Transport based on Segment Routing for Traffic E";
  }
  enum "RSVP-TE" {
    value 5;
    description "Transport based on RSVP for Traffic Engineering";
  }
  enum "unknown" {
    value 6;
    description "Transport UNKNOWN";
  }

}
description
  "This are writable attributes used to identify
  transport protocols underlaying the service";
}

typedef area-address {
type string {
  pattern '[0-9A-Fa-f]{2}(\.[0-9A-Fa-f]{4}){0,6}';
}
description
  "This type defines the area address format.";
}

typedef isis-level {
type enumeration {
  enum level1 {
    value 0;
    description "ISIS level 1";
  }
  enum level2 {
   value 1;
   description "ISIS level 2";
  }
  enum level1-2 {
   value 2;
   description "ISIS level 1 and 2";
  }
}
description
"Defines the ISIS level for interface and system";
}

typedef ie-type {
type enumeration {
  enum import {
    value 0;
    description
      "Import a routing profile.";
  }
  enum export {
    value 1;
    description
      "Export a routing profile";
  }
  enum both {
    value 2;
    description
      "Import/Export a routing profile";
  }
}
description
  "Defines Import-Export routing profiles.
   Those are able to be reused between vpn-nodes.";
}

typedef operational-type {
type enumeration {
  enum up {
    value 0;
    description
      "Operational status UP/Enabled.";
  }
  enum down {
    value 1;
    description
      "Operational status DOWN/Disabled";
  }
  enum unknown {
    value 2;
    description
      "Operational status UNKNOWN";
  }
}
description
  "This is a read-only attribute used to determine the
   status of a particular element.";
}

