grouping multicast-rp-group-cfg {
choice group-format {
  mandatory true;
  case group-prefix {
    leaf group-address {
      type inet:ip-prefix;
      description
        "A single multicast group prefix.";
    }
  }
  case startend {
    leaf group-start {
      type inet:ip-address;
      description
        "The first multicast group address in
         the multicast group address range.";
    }
    leaf group-end {
      type inet:ip-address;
      description
        "The last multicast group address in
         the multicast group address range.";
    }
  }
  description
    "Choice for multicast group format.";
}
description
  "This grouping defines multicast group or
   multicast groups for RP-to-group mapping.";
}

grouping vpn-service-multicast {
container multicast {
  if-feature "l3vpn-svc:multicast";
  leaf enabled {
    type boolean;
    default "false";
    description
      "Enables multicast.";
  }
    leaf-list tree-flavor {
      type identityref {
        base l3vpn-svc:multicast-tree-type;
      }
      description
        "Type of tree to be used.";
    }
  container rp {
    container rp-group-mappings {
      list rp-group-mapping {
        key "id";
        leaf id {
          type uint16;
          description
            "Unique identifier for the mapping.";
        }
        container provider-managed {
          leaf enabled {
            type boolean;
            default "false";
            description
              "Set to true if the Rendezvous Point (RP)
               must be a provider-managed node.  Set to false
               if it is a customer-managed node.";
          }
          leaf rp-redundancy {
            type boolean;
            default "false";
            description
              "If true, a redundancy mechanism for the RP
               is required.";
          }
          leaf optimal-traffic-delivery {
            type boolean;
            default "false";
            description
              "If true, the SP must ensure that
               traffic uses an optimal path.  An SP may use
               Anycast RP or RP-tree-to-SPT switchover
               architectures.";
          }
          container anycast {
            when "../rp-redundancy = 'true' and ../optimal-traffic-delivery = 'true'"{
              description "Only applicable if RP 
              redundancy is enabled and delivery 
              through optimal path is activated.";
            }
            leaf local-address{
              type inet:ip-address;
              description 
              "IP local address for PIM RP. 
              Usually, it corresponds to router ID or primary address";
            }
            leaf-list rp-set-address {
              type inet:ip-address;
              description
              "Address other RP routers that share
               the same RP IP address.";
            }
            description 
            "PIM Anycast-RP parameters.";
          }  
          description
            "Parameters for a provider-managed RP.";
        }
        leaf rp-address {
          when "../provider-managed/enabled = 'false'" {
            description
              "Relevant when the RP is 
              not provider-managed.";
          }
          type inet:ip-address;
          mandatory true;
          description
            "Defines the address of the RP.
             Used if the RP is customer-managed.";
        }
        container groups {
          list group {
            key "id";
            leaf id {
              type uint16;
              description
                "Identifier for the group.";
            }
            uses multicast-rp-group-cfg;
            description
              "List of multicast groups.";
          }
          description
            "Multicast groups associated with the RP.";
        }
        description
          "List of RP-to-group mappings.";
      }
      description
        "RP-to-group mappings parameters.";
    }
    container rp-discovery {
      leaf rp-discovery-type {
        type identityref {
          base l3vpn-svc:multicast-rp-discovery-type;
        }
        default "l3vpn-svc:static-rp";
        description
          "Type of RP discovery used.";
      }
      container bsr-candidates {
        when "derived-from-or-self(../rp-discovery-type, "
           + "'l3vpn-ntw:bsr-rp')" {
          description
            "Only applicable if discovery type
             is BSR-RP.";
        }
        leaf-list bsr-candidate-address {
          type inet:ip-address;
          description
            "Address of BSR candidate.";
        }
        description
          "Container for List of Customer
           BSR candidate's addresses.";
        }   
      description
        "RP discovery parameters.";
    }
    description
      "RP parameters.";
    }  
    container msdp {
      if-feature msdp;
      leaf enabled {
        type boolean;
        default false;
        description
         "If true, Multicast Source 
         Discovery Protocol (MSDP) 
         protocol is activated.";
      }
      leaf peer {
        type inet:ip-address;
        description
          "IP address of the MSDP peer.";
      }
      leaf local-address{
        type inet:ip-address;
        description
         "IP address of the local end. 
         This local address must be 
         configured on the node.";
      }
      description 
        "MSDP parameters.";
    }  
  description
    "Multicast global parameters for the VPN service.";
}
description
  "Grouping for multicast VPN definition.";
}

