grouping site-service-qos-profile {
container qos {
  if-feature "l3vpn-svc:qos";
  container qos-classification-policy {
    list rule {
      key "id";
      ordered-by user;
      leaf id {
        type string;
        description
          "A description identifying the
           qos-classification-policy rule.";
      }
      choice match-type {
        default "match-flow";
        case match-flow {
          //uses l3vpn-svc:flow-definition;
         choice l3 {
           container ipv4 {
             uses packet-fields:acl-ip-header-fields;
             uses packet-fields:acl-ipv4-header-fields;
             description
               "Rule set that matches IPv4 header.";
           }
           container ipv6 {
             uses packet-fields:acl-ip-header-fields;
             uses packet-fields:acl-ipv6-header-fields;
             description
               "Rule set that matches IPv6 header.";
           }
           description
             "Either IPv4 or IPv6.";
         }
         choice l4 {
           container tcp {
             uses packet-fields:acl-tcp-header-fields;
             uses ports;
             description
               "Rule set that matches TCP header.";
           }
           container udp {
             uses packet-fields:acl-udp-header-fields;
             uses ports;
             description
               "Rule set that matches UDP header.";
           }
           description
             "Can be TCP or UDP";
         }
        }
        case match-application {
          leaf match-application {
            type identityref {
              base l3vpn-svc:customer-application;
            }
            description
              "Defines the application to match.";
          }
        }
        description
          "Choice for classification.";
      }
      leaf target-class-id {
        type string;
        description
          "Identification of the class of service.
           This identifier is internal to the administration.";
      }
      description
        "List of marking rules.";
    }
    description
      "Configuration of the traffic classification policy.";
  }
  container qos-profile {
    choice qos-profile {
      description
        "Choice for QoS profile.
         Can be standard profile or customized profile.";
      case standard {
        description
          "Standard QoS profile.";
        leaf profile {
          type leafref {
            path "/l3vpn-ntw/vpn-profiles/valid-provider-identifiers"
               + "/qos-profile-identifier/id";
          }
          description
            "QoS profile to be used.";
        }
        leaf direction {
          type identityref {
            base l3vpn-svc:qos-profile-direction;
          }
          default "l3vpn-svc:both";
          description
            "The direction to which the QoS profile
             is applied.";
        }
      }
      case custom {
        description
          "Customized QoS profile.";
        container classes {
          if-feature "l3vpn-svc:qos-custom";
          list class {
            key "class-id";
            leaf class-id {
              type string;
              description
                "Identification of the class of service.
                 This identifier is internal to the
                 administration.";
            }
            leaf direction {
              type identityref {
                base l3vpn-svc:qos-profile-direction;
              }
              default "l3vpn-svc:both";
              description
                "The direction to which the QoS profile
                 is applied.";
            }
            leaf rate-limit {
              type decimal64 {
                fraction-digits 5;
                range "0..100";
              }
              units "percent";
              description
                "To be used if the class must be rate-limited.
                 Expressed as percentage of the service
                 bandwidth.";
            }
            container latency {
              choice flavor {
                case lowest {
                  leaf use-lowest-latency {
                    type empty;
                    description
                      "The traffic class should use the path with the
                       lowest latency.";
                  }
                }
                case boundary {
                  leaf jitter-boundary {
                    type uint16;
                    units "msec";
                    default "400";
                    description
                      "The traffic class should use a path with a
                       defined maximum latency.";
                  }
                }
                description
                  "Latency constraint on the traffic class.";
              }
              description
                "Latency constraint on the traffic class.";
            }
            container jitter {
              choice flavor {
                case lowest {
                  leaf use-lowest-jitter {
                    type empty;
                    description
                      "The traffic class should use the path with the
                       lowest jitter.";
                  }
                }
                case boundary {
                  leaf latency-boundary {
                    type uint32;
                    units "usec";
                    default "40000";
                    description
                      "The traffic class should use a path with a
                       defined maximum jitter.";
                  }
                }
                description
                  "Jitter constraint on the traffic class.";
              }
              description
                "Jitter constraint on the traffic class.";
            }
            container bandwidth {
              leaf guaranteed-bw-percent {
                type decimal64 {
                  fraction-digits 5;
                  range "0..100";
                }
                units "percent";
                mandatory true;
                description
                  "To be used to define the guaranteed bandwidth
                   as a percentage of the available service bandwidth.";
              }
              leaf end-to-end {
                type empty;
                description
                  "Used if the bandwidth reservation
                   must be done on the MPLS network too.";
              }
              description
                "Bandwidth constraint on the traffic class.";
            }
            description
              "List of classes of services.";
          }
          description
            "Container for list of classes of services.";
        }
      }
    }
    description
      "QoS profile configuration.";
  }
  description
    "QoS configuration.";
}
description
  "This grouping defines QoS parameters for a site.";
}

